var util = require('util');

function buildHostName(destination) {
  var host = destination.host || 'missing host',
      port = destination.port || 'missing port';
  return [host, port].join(':');
}

module.exports = function(destinations) {
  var items = [];
  if (util.isArray(destinations)) {
    items = destinations.map(buildHostName);
  } else if (typeof destinations === 'object') {
    items.push(buildHostName(destinations));
  } else {
    items.push(destinations);
  }
  return items.join(', ');
};
