var expect = require('chai').expect,
    appleconnect = require('../lib/appleconnect');

describe("appleconnect", function() {
  describe('authenticate', function() {
    it('is a function factory', function() {
      expect(typeof(appleconnect.authenticate)).to.equal('function');
      expect(typeof(appleconnect.authenticate())).to.equal('function');
    });
  });
});
